function setAttributes(el, attrs) {
    for(var key in attrs) {
      el.setAttribute(key, attrs[key]);
    }
}

function insertBtn(btnGroupId,barId,text,playerInd,start,end){
    var btnsField=document.getElementById(btnGroupId);
    var btnProgress=document.createElement("div");
    var btnBar=document.createElement("div");
    setAttributes(btnProgress,{
        "class":"btnProgress",
        "onclick":`playDuration(${playerInd},${start},${end},"${barId}")`});
    setAttributes(btnBar,{
        "class":"btnBar",
        "id":barId});
    var btnLabel=document.createElement("div");
    setAttributes(btnLabel,{"class":"textLabel"});
    btnLabel.innerHTML=text;
    btnProgress.appendChild(btnBar);
    btnProgress.appendChild(btnLabel);
    btnsField.appendChild(btnProgress);
}