// This code loads the IFrame Player API code asynchronously.
var tag = document.createElement('script');

tag.src = "https://www.youtube.com/iframe_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

// This function creates an <iframe> (and YouTube player)
//    after the API code downloads.
var playerDivs = document.querySelectorAll(".player"); // get .player nodes
var players = new Array(playerDivs.length);
var currentPlayer=null;
var currentBar=null;
var timerId=null;
var triggerByBtn=false;

// when youtube stuff is ready
function onYouTubeIframeAPIReady() {  
  // create yt players
    playerDivs.forEach(function(e, i) { // forEach ...
        players[i] = new YT.Player(e.id, {
            height: '350',
            width: '700',
            videoId: plist[i],
            events: { 
                'onStateChange': switchPlayer
            }
        });
    })  
}

function switchPlayer(event) { //handle player playing
    if(event.data === 1){
        if(currentPlayer){
            currentPlayer.pauseVideo();
        }
        currentPlayer = event.target;

        if(triggerByBtn){
            triggerByBtn=false;
        }
        else if(timerId !==null){
            clearInterval(timerId);
            timerId = null;
            currentBar.style.width="0%";
            currentBar=null;
        }
    }
    //console.log(event.data);
} 

function playDuration(playerInd,start,end,barId){
    var player=players[playerInd]; //take over player and bar
    var btnBar=document.getElementById(barId);
    currentBar=btnBar;

    player.seekTo(start);
    triggerByBtn=true;
    player.playVideo();

    var currentPercent,lastPercent=0;
    timerId=setInterval(()=>{
        currentPercent = Math.round((player.getCurrentTime()-start)/(end-start)*100);
        if(currentPercent>=0 && currentPercent<=100){
            //console.log(currentPercent);
            if(currentPercent >= lastPercent){ //prevent bar go back
                lastPercent=currentPercent;
                btnBar.style.width=`${currentPercent}%`;
            }
            if(currentPercent == 100){ //to the end
                player.pauseVideo();
                currentPlayer=null;
                btnBar.style.width="0%"; //recover bar
                currentBar=null; //clear currentBar
                clearInterval(timerId);
                timerId=null; //set timer as null to easily check timer is setup or not
            }
        }
    },10); // set every certain time check playtime
}